import {
    Heading,
    Text,
    Box
  } from "@chakra-ui/react";
  import { useState, useEffect } from "react";

  export default function Comment() {
    const [data, setData] = useState(null);
    const [error, setError] = useState(null);
  
    useEffect(() => {
        fetch("http://localhost:8000/api/comment/") 
        .then((response) => {
          return response.json();
        })
        .then((actualData) => {
          console.log(actualData);
          setData(actualData)
        })
        .catch((err) => {
          console.error(err);
          setError(err.message);
        });
    }, []);
  
    return (
      <div className="App">
        <h1>API Posts</h1>
        {error && (
          <div>{`Ошибка получения данных - ${error}`}</div>
        )}
        <ul>
          {data &&
            data.map(({ id, name, comment }) => (
              <li key={id}>
                <h3>{name}</h3>
                <h3>{comment}</h3>
              </li>
            ))}
        </ul>
      </div>
    );

    return (
      <>
        <Box maxW='32rem'>
        <Heading mb={2}>1</Heading>
        <Text fontSize='xl'>123 {getComments()}</Text>
        </Box>
      </>
    );
  }




